function calculateInsurance(){var e=100*Number(document.getElementById("power").value)/Number(document.getElementById("age").value)+50,t=120*Number(document.getElementById("power").value)/Number(document.getElementById("age").value)+100,n=150*Number(document.getElementById("power").value)/(Number(document.getElementById("age").value)+3)+50,u="Dear mr/mss "+document.getElementById("name").value;switch(document.getElementById("country").value){case"0":document.getElementById("ergebnis").innerHTML=u+" your quoute is: "+e.toFixed(2)+" €";break;case"1":document.getElementById("ergebnis").innerHTML=u+" your quoute is: "+t.toFixed(2)+" €";break;case"2":document.getElementById("ergebnis").innerHTML=u+" your quoute is: "+n.toFixed(2)+" €"}document.getElementById("ergebnis").style.visibility="visible"}document.getElementById("submit").addEventListener("click",calculateInsurance);