function calculateInsurance(){var e=100*Number(document.getElementById("power").value)/Number(document.getElementById("age").value)+50,n=120*Number(document.getElementById("power").value)/Number(document.getElementById("age").value)+100,t=150*Number(document.getElementById("power").value)/(Number(document.getElementById("age").value)+3)+50;switch(document.getElementById("country").value){case"0":document.getElementById("ergebnis").innerHTML="deine versicherungssummer beläuft sich auf "+e;break;case"1":document.getElementById("ergebnis").innerHTML="deine versicherungssummer beläuft sich auf "+n;break;case"2":document.getElementById("ergebnis").innerHTML="deine versicherungssummer beläuft sich auf "+t}document.getElementById("ergebnis").style.visibility="visible"}document.getElementById("submit").addEventListener("click",calculateInsurance);